SELECT o.id, o.capacity, o.occupied_capacity, -- 从script表获取地址信息 s.code_hash as lock_code_hash, s.args as lock_args, s.hash_type as lock_hash_type, -- Cellbase奖励相关字段 -- t.block_number - 11 as target_block_number, -- b.base_reward, -- b.commit_reward, -- b.proposal_reward, -- b.secondary_reward, -- 状态判断：如果is_spent为1则表示已消耗，否则为活动状态 o.is_spent AS status, -- 消耗的交易哈希 CASE WHEN o.is_spent = 1 THEN '0x' || encode(o.consumed_tx_hash, 'hex') END AS consumed_tx_hash, -- 生成的交易哈希 '0x' || encode(o.tx_hash, 'hex') AS generated_tx_hash, o.output_index AS cell_index, CASE WHEN oe.cell_type is null THEN 0 ELSE oe.cell_type END as cell_type, o.tx_id as transaction_id FROM output o left join script s ON o.lock_script_id = s.id left join output_extend oe ON o.id = oe.output_id -- JOIN -- block b ON t.block_id = b.id WHERE
o.tx_id in
#{item}

SELECT id, capacity, occupied_capacity, lock_code_hash, lock_args, lock_hash_type, status, consumed_tx_hash, generated_tx_hash, cell_index, cell_type, args, code_hash, hash_type, transaction_id from (SELECT o.id, o.capacity, o.occupied_capacity, -- 从script表获取地址信息 s.code_hash AS lock_code_hash, s.args AS lock_args, s.hash_type AS lock_hash_type, -- Cellbase奖励相关字段 -- b.base_reward, -- b.commit_reward, -- b.proposal_reward, -- b.secondary_reward, -- 状态判断：如果is_spent为1则表示已消耗，否则为活动状态 o.is_spent AS status, -- 消耗的交易哈希 CASE WHEN o.is_spent = 1 THEN '0x' || encode(o.consumed_tx_hash, 'hex') END AS consumed_tx_hash, -- 生成的交易哈希 '0x' || encode(o.tx_hash, 'hex') AS generated_tx_hash, o.output_index AS cell_index, CASE WHEN oe.cell_type is null THEN 0 ELSE oe.cell_type END AS cell_type, '0x' || encode(type.args, 'hex') AS args, '0x' || encode(type.code_hash, 'hex') AS code_hash, type.hash_type AS hash_type, o.tx_id AS transaction_id, ROW_NUMBER() OVER (PARTITION BY o.tx_id ORDER BY o.output_index ASC) AS rn FROM output o left join script s ON o.lock_script_id = s.id left join script type on o.type_script_id = type.id left join output_extend oe ON o.id = oe.output_id -- JOIN -- block b ON t.block_id = b.id WHERE
o.tx_id in
#{item}
) t WHERE rn <= #{size} ORDER BY transaction_id, cell_index ASC;
